mapper.select:
SELECT c.category_id, c.member_id, c.category_name, c.category_status, c.category_created, c.category_updated, b.board_id, b.board_title, b.board_body, b.board_created, b.board_updated, b.board_status, b.board_score, b.board_view, m.member_id, m.member_alias FROM mobile.category c LEFT JOIN mobile.board b ON b.category_id = c.category_id LEFT JOIN mobile.member m ON b.member_id = m.member_id WHERE c.state_del = 'N' AND b.state_del = 'N' AND m.state_del = 'N'
AND c.category_id = #{categoryId}
AND m.member_id = #{memberId}
AND b.board_id = #{boardId}
AND c.category_name = #{categoryName}
AND b.board_status = #{boardStauts}
AND c.category_status = #{categoryStatus}
